import { useEffect, useState } from 'react';
import api from '../api';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/AdminOrders.css';

export default function AdminOrders() {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);

    const fetchOrders = async () => {
        try {
            const token = localStorage.getItem('token');
            const res = await api.get('/admin/orders', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setOrders(res.data);
        } catch (err) {
            console.error('Ошибка загрузки заказов:', err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchOrders();
    }, []);

    const handleDeleteOrder = async (orderId) => {
        if (!window.confirm('Вы уверены, что хотите удалить этот заказ?')) {
            return;
        }

        try {
            await api.delete(`/admin/orders/${orderId}`);
            setOrders(orders.filter(order => order.id !== orderId));
        } catch (err) {
            setError(err.response?.data?.error || 'Не удалось удалить заказ');
        }
    };


    const updateStatus = async (orderId, newStatus) => {
        try {
            const token = localStorage.getItem('token');
            await api.patch(`/admin/orders/${orderId}`, { status: newStatus }, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setOrders(prev =>
                prev.map(order =>
                    order.id === orderId ? { ...order, status: newStatus } : order
                )
            );
        } catch (err) {
            console.error('Ошибка обновления статуса:', err);
            alert('Не удалось обновить статус');
        }
    };

    return (
        <>
            <Header />
            <main className="admin-orders-container">
                <h2>Заявки пользователей</h2>
                {loading ? (
                    <p>Загрузка...</p>
                ) : orders.length === 0 ? (
                    <p>Нет заявок.</p>
                ) : (
                    <ul className="admin-orders-list">
                        {orders.map(order => (
                            <li key={order.id} className="admin-order-item">
                                <p><strong>Пользователь:</strong> {order.user}</p>
                                <p><strong>Услуга:</strong> {order.service}</p>
                                <p><strong>Статус:</strong> {order.status}</p>
                                <select
                                    value={order.status}
                                    onChange={(e) => updateStatus(order.id, e.target.value)}
                                >
                                    <option value="created">created</option>
                                    <option value="in progress">in progress</option>
                                    <option value="completed">completed</option>
                                </select>
                                <div className="order-actions">
                                        <button 
                                            onClick={() => handleDeleteOrder(order.id)}
                                            className="delete-btn"
                                        >
                                            Удалить заказ
                                        </button>
                                </div>
                            </li>
                        ))}
                    </ul>
                )}
            </main>
            <Footer />
        </>
    );
}
